import { AnnouncementCard, TodosCard } from 'components/Card';
import HorizontalAvatarList from 'components/HorizontalAvatarList';
import MapWithBubbles from 'components/MapWithBubbles';
import Page from 'components/Page';
import ProductMedia from 'components/ProductMedia';
import SupportTicket from 'components/SupportTicket';
import UserProgressTable from 'components/UserProgressTable';
import CountryProgressTable from 'components/CountryProgressTable';


import { IconWidget, NumberWidget } from 'components/Widget';
import { getStackLineChart, stackLineChartOptions } from 'demos/chartjs';
import {
  avatarsData,
  chartjs,
  productsData,
  supportTicketsData,
  todosData,
  userProgressTableData,
} from 'demos/dashboardPage';
import React from 'react';
import { Bar, Line } from 'react-chartjs-2';
import {
  MdSentimentSatisfied,
  MdPlace,
  MdEnhancedEncryption,
  MdWhatshot,
  MdLightbulbOutline,
  MdMailOutline,
  MdPlayCircleOutline,
  MdRadio,
  MdSnooze,
  MdThumbsUpDown,
  MdThumbUp,
  MdBubbleChart,
  MdInsertChart,
  MdPersonPin,
  MdPieChart,
  MdRateReview,
  MdShare,
  MdShowChart,
} from 'react-icons/md';
import InfiniteCalendar from 'react-infinite-calendar';
import {
  Badge,
  Button,
  Card,
  CardBody,
  CardDeck,
  CardGroup,
  CardHeader,
  CardTitle,
  Col,
  ListGroup,
  ListGroupItem,
  Row,
} from 'reactstrap';
import {  countryProgressTableData } from 'demos/dashboardCountry';


import { getColor } from 'utils/colors';



const today = new Date();
const lastWeek = new Date(
  today.getFullYear(),
  today.getMonth(),
  today.getDate() - 7,
);



class DashboardPage extends React.Component {
  constructor() {
    super();
    this.state = {
      paisItems: {},
      itemsTotalesFormateados: {},
      itemsTotales: {},
      iso2: "COL",
      iso3: "CO",
      paisItemsTotalesFormateados: {},
      paisItemsTotales: {},

    };
  }

  handleResponseError(response) {
    throw new Error("HTTP error, status = " + response.status);
  }
  handleError(error) {
    console.log(error.message);
  }

  cargaValoresTotales(request) {
    fetch(request)
      .then(response => {
        if (!response.ok) {
          this.handleResponseError(response);
        }
        return response.json();
      })
      .then(json => {
        console.log("cargaValoresTotales resultados : " + JSON.stringify(json));
        let itemsTotalesFormateados =
        {
          "confirmed": json.confirmed.toLocaleString(),
          "deaths": json.deaths.toLocaleString(),
          "recovered": json.recovered.toLocaleString()
        };

        this.setState({
          itemsTotalesFormateados: itemsTotalesFormateados,
          itemsTotales: json
        })
        let url = 'https://wuhan-coronavirus-api.laeyoung.endpoint.ainize.ai/jhu-edu/latest';
        let iso = 'iso3';
        let valueIso = 'COL';
        const request1 = new Request(
          url + '?' + iso + '=' + valueIso, {
          method: 'GET',
        });
        this.cargaIsoTotales(request1);

      })
      .catch(error => {
        this.handleError(error);
      });
  }



  cargaIsoTotales(request) {
    fetch(request)
      .then(response => {
        if (!response.ok) {
          this.handleResponseError(response);
        }
        return response.json();
      })
      .then(json => {
        console.log("cargaIsoTotales los resultados : " + JSON.stringify(json[0]));
        let paisItemsTotalesFormateados =
        {
          "confirmed": json[0].confirmed.toLocaleString(),
          "deaths": json[0].deaths.toLocaleString(),
          "recovered": json[0].recovered.toLocaleString()
        };

        this.setState({
          paisItems: json[0],
          paisItemsTotalesFormateados: paisItemsTotalesFormateados
        })


      })
      .catch(error => {
        this.handleError(error);
      });
  }
  componentDidMount() {
    // this is needed, because InfiniteCalendar forces window scroll
    window.scrollTo(0, 0);
    const request = new Request('https://wuhan-coronavirus-api.laeyoung.endpoint.ainize.ai/jhu-edu/brief', {
      method: 'GET'
    });
    this.cargaValoresTotales(request);
  }

  render() {
    const primaryColor = getColor('primary');
    const secondaryColor = getColor('secondary');

    return (
      <Page
        className="DashboardPage"
        title={this.state.paisItems.countryregion + ' - ' + 'Seguimiento de coronavirus'}
        breadcrumbs={[{ name: 'Mapa', active: true }]}
      >
        <Row>
          <Col lg={4} md={6} sm={6} xs={12}>
            <NumberWidget
              title="Total casos confirmados"
              subtitle="Casos globales"
              number={this.state.itemsTotalesFormateados.confirmed}
              color="primary"
              progress={{
                value: Number.parseFloat((this.state.paisItemsTotalesFormateados.confirmed * 100) / this.state.itemsTotales.confirmed).toFixed(4)
                ,
                label: 'Casos ' + this.state.paisItems.countryregion
                  + ' % Casos globales',

              }}
            />
          </Col>

          <Col lg={4} md={6} sm={6} xs={12}>
            <NumberWidget
              title="Muertes totales"
              subtitle="Casos globales"
              number={this.state.itemsTotalesFormateados.deaths}
              color="danger"
              progress={{
                value: (Number.parseFloat(this.state.paisItemsTotalesFormateados.deaths * 100) / this.state.itemsTotales.deaths).toFixed(4),
                label: 'Casos ' + this.state.paisItems.countryregion
                  + ' % Casos globales',
              }}
            />
          </Col>

          <Col lg={4} md={6} sm={6} xs={12}>
            <NumberWidget
              title="Total casos recuperados"
              subtitle="Casos globales"
              number={this.state.itemsTotalesFormateados.recovered}
              color="success"
              progress={{
                value: (Number.parseFloat(this.state.paisItemsTotalesFormateados.recovered * 100) / this.state.itemsTotales.recovered).toFixed(4),
                label: 'Casos ' + this.state.paisItems.countryregion
                  + ' % Casos globales',
              }}
            />
          </Col>
        </Row>


        <Row>
          <Col lg={4} md={6} sm={6} xs={12}>
            <IconWidget
              bgColor='primary'
              icon={MdEnhancedEncryption}
              iconProps={{ size: 50 }}
              title={'Total : ' + this.state.paisItemsTotalesFormateados.confirmed}
              subtitle="Confirmados"
            />
          </Col>

          <Col lg={4} md={6} sm={6} xs={12}>
            <IconWidget
              bgColor='danger'
              icon={MdWhatshot}
              iconProps={{ size: 50 }}
              title={'Total : ' + this.state.paisItemsTotalesFormateados.deaths}
              subtitle="Muertes"
            />
          </Col>

          <Col lg={4} md={6} sm={6} xs={12}>
            <IconWidget
              bgColor='success'
              icon={MdSentimentSatisfied}
              iconProps={{ size: 50 }}
              title={'Total : ' + this.state.paisItemsTotalesFormateados.recovered}
              subtitle="Recuperados"
            />
          </Col>
        </Row>



        <Row>
          <Col lg="4" md="12" sm="12" xs="12">

            <Card>
              <CardHeader>Seleccione un país</CardHeader>
              <CardBody>
                <CountryProgressTable
                  headers={[
                    'Pais',
                    <MdPlace size={25} />,
                    'date',
                    'participation',
                    '%',
                  ]}
                  countryData={countryProgressTableData}
                  usersData={userProgressTableData}
                />
              </CardBody>
            </Card>

          </Col>

          <Col lg="8" md="12" sm="12" xs="12">
            <Card inverse className="bg-gradient-primary">
              <CardHeader className="bg-gradient-primary">
                Casos confirmados acumulados
              </CardHeader>
              <CardBody>
                <MapWithBubbles />
              </CardBody>
            </Card>
          </Col>
        </Row>



      </Page>
    );
  }
}
export default DashboardPage;
