import MapWithBubbles from 'components/MapWithBubbles';
import Page from 'components/Page';
import CountryProgressTable from 'components/CountryProgressTable';
import { IconWidget, NumberWidget } from 'components/Widget';
import React from 'react';
import {
  MdSentimentSatisfied,
  MdPlace,
  MdEnhancedEncryption,
  MdWhatshot,
} from 'react-icons/md';

import {
  Card,
  CardBody,
  CardHeader,
  Col,
  Row,
} from 'reactstrap';
import { countryProgressTableData } from 'demos/dashboardCountry';

class DashboardPage extends React.Component {
  constructor() {
    super();
    this.state = {
      paisItemsOrdenados: [],
      paisItems: {},
      itemsTotalesFormateados: {},
      itemsTotales: {},
      iso2: "COL",
      iso3: "CO",
      paisItemsTotalesFormateados: {},
      paisItemsTotales: {},
    };
  }

  callback = (countrycode) => {
    let url = 'https://wuhan-coronavirus-api.laeyoung.endpoint.ainize.ai/jhu-edu/latest';
    let iso = 'iso3';
    let valueIso = countrycode.iso3;
    const request1 = new Request(
      url + '?' + iso + '=' + valueIso, {
      method: 'GET',
    });
    this.cargaIsoTotales(request1);
  }

  handleResponseError(response) {
    throw new Error("HTTP error, status = " + response.status);
  }
  handleError(error) {
    console.log(error.message);
  }

  cargaValoresTotales(request) {
    fetch(request)
      .then(response => {
        if (!response.ok) {
          this.handleResponseError(response);
        }
        return response.json();
      })
      .then(json => {
        console.log("cargaValoresTotales resultados : " + JSON.stringify(json));
        let itemsTotalesFormateados =
        {
          "confirmed": json.confirmed.toLocaleString(),
          "deaths": json.deaths.toLocaleString(),
          "recovered": json.recovered.toLocaleString()
        };

        this.setState({
          itemsTotalesFormateados: itemsTotalesFormateados,
          itemsTotales: json
        })
        let url = 'https://wuhan-coronavirus-api.laeyoung.endpoint.ainize.ai/jhu-edu/latest';
        let iso = 'iso3';
        let valueIso = 'COL';
        const request1 = new Request(
          url + '?' + iso + '=' + valueIso, {
          method: 'GET',
        });
        this.cargaIsoTotales(request1);

      })
      .catch(error => {
        this.handleError(error);
      });
  }

  cargaIsoTotales(request) {
    fetch(request)
      .then(response => {
        if (!response.ok) {
          this.handleResponseError(response);
        }
        return response.json();
      })
      .then(json => {
        console.log("cargaIsoTotales los resultados : " + JSON.stringify(json[0]));
        let paisItemsTotalesFormateados =
        {
          "confirmed": json[0].confirmed.toLocaleString(),
          "deaths": json[0].deaths.toLocaleString(),
          "recovered": json[0].recovered.toLocaleString()
        };
        this.setState({
          paisItems: json[0],
          paisItemsTotalesFormateados: paisItemsTotalesFormateados
        })
      })
      .catch(error => {
        this.handleError(error);
      });
  }


  ordenarAscTable(data, key, orden) {
    return data.sort(function (a, b) {
      var x = a[key],
        y = b[key];

      if (orden === 'asc') {
        return ((x < y) ? -1 : ((x > y) ? 1 : 0));
      }

      if (orden === 'desc') {
        return ((x > y) ? -1 : ((x < y) ? 1 : 0));
      }
    });
  }

  componentDidMount() {
    window.scrollTo(0, 0);
    const request = new Request('https://wuhan-coronavirus-api.laeyoung.endpoint.ainize.ai/jhu-edu/brief', {
      method: 'GET'
    });
    this.cargaValoresTotales(request);
    let jsonArrayLista = countryProgressTableData;
    this.ordenarAscTable(jsonArrayLista, 'countryregion', 'asc');
    this.setState({ paisItemsOrdenados: jsonArrayLista })
  }

  render() {
    return (
      <div></div>
      );
  }
}
export default DashboardPage;
